# https://leetcode.com/problems/longest-palindromic-substring/description/
# https://leetcode.com/problems/longest-palindromic-substring/solution/ (READ APPROACH 4)
class Solution(object):
    def longestPalindrome(self, s):
        """
        :type s: str
        :rtype: str
        """
        slen = len(s)
        if slen == 1:
            return s
        arr = [[False] * slen for x in range(slen)]
        for i in range(slen):
            arr[i][i] = True

        l = 1
        maxpal = 0
        pos = ()
        while l <= slen:
            i = 0
            j = l
            while j < slen:
                if s[i] == s[j]:
                    if i == j - 1:
                        # CONSIDER THE EDGE CASE WHERE ROW = COL - 1,
                        '''
                          0 1 2 3
                        0 T F F F
                        1   T F T
                        2     T T
                        3       T   
                        Consider element arr[2][3], it cant rely on i+1,j-1
                        '''
                        # YOU HAVE NO SOUTHWEST DIAGONAL ELEMENTS TO DEPEND ON
                        arr[i][j] = True
                    else:
                        arr[i][j] = arr[i + 1][j - 1]
                    if arr[i][j] is True and maxpal < abs(i - j):
                        maxpal = abs(i - j)
                        pos = (i, j)
                else:
                    arr[i][j] = False

                i += 1
                j += 1

            l += 1

        if len(pos) == 0:
            return s[0]
        return s[pos[0]:pos[1] + 1]

s = Solution()
print(s.longestPalindrome("ababababa"))
print(s.longestPalindrome("ababac"))
print(s.longestPalindrome("madame"))
print(s.longestPalindrome("car"))
print(s.longestPalindrome("malayalame"))
print(s.longestPalindrome("goddoggty"))
