# https://leetcode.com/problems/longest-palindromic-substring/description/
class Solution(object):
    def longestPalindrome(self, s):
        """
        :type s: str
        :rtype: str
        """
        slen = len(s)
        if slen == 1:
            return s
        arr = [[False] * slen for x in range(slen)]
        for i in range(slen):
            arr[i][i] = True

        l = 1
        pal = ''
        while l <= slen:
            i = 0
            j = l
            while j < slen:
                if s[i] == s[j]:
                    # CONSIDER THE EDGE CASE WHERE ROW = COL - 1,
                    '''
                      0 1 2 3
                    0 T F F F
                    1   T F T
                    2     T T
                    3       T   
                    Consider element arr[2][3], it cant rely on i+1,j-1
                    '''
                    # YOU HAVE NO SOUTHEAST DIAGONAL ELEMENTS TO DEPEND ON
                    if i == j - 1:
                        arr[i][j] = True
                    else:
                        arr[i][j] = arr[i + 1][j - 1]
                else:
                    arr[i][j] = False

                i += 1
                j += 1

            l += 1

        res = {}
        for i in range(slen):
            for j in range(slen - 1, -1, -1):
                if arr[i][j] is True:
                    res[abs(i - j)] = (i, j)

        maxkey = 0
        for k in res.keys():
            if maxkey < k:
                maxkey = k
        return s[res[maxkey][0]:res[maxkey][1] + 1]

s = Solution()
s.longestPalindrome("ababababa")
import time
tick = time.time()
s.longestPalindrome("")
tock = time.time()
print("Time taken {0}".format(tock - tick))